class Card extends StatefulWidget {
  Card(this.items,{Key key}) : super(key: key);
  final Map items;

  CardState createState() => CardState(items);
}

class CardState extends State<Card>
  with TickerProviderStateMixin {
    CardState(this.items);
    AnimationController controller;
    AnimationController controller1;
    Animation growAnimation;
    Animation qwer;
    bool front = true;
    int stack = 0;
    Map items;
    String text ;

  void tickfront() async {
    if(front){
    controller.forward();
    await new Future.delayed(Duration(milliseconds: 200), () {
      front = !front;
      setState(() {});
      controller1.reset();
      stack=1;controller1.forward();});}

    else if (!front){
      print("!front");
      
    }
    }

  void tickback() async {
    controller1.reverse();
    controller.reset();
    controller.forward();
    await new Future.delayed(Duration(milliseconds: 200), () {
      front = !front;
      setState(() {});
      stack=0;
      controller.reverse();});
    print("!232");
    }

  @override
  void initState() {
    super.initState();
    controller1 =
      AnimationController(vsync: this, duration: const Duration(milliseconds : 200))
      ..addListener(() {
        setState(() {});
        });
    controller =
      AnimationController(vsync: this, duration: const Duration(milliseconds : 200))
        ..addListener(() {
          setState(() {});
          });
    qwer = Tween<double>(
      begin: 0.0, 
      end: 0.5)
      .animate(controller);

    growAnimation = Tween<double>(
      begin: 0.5, 
      end: 0.0)
      .animate(controller1);
    }

  String keys = '';

  @override
  Widget build(BuildContext context) {
    for (var item in items["keys"]) {
      keys = keys + item + "\n";
    }
    text = items["title"] + "\n" + items["short description"] + "\n" + items["ful description"] + "\n" + keys  + "\n" +  items["case"];
    Widget a = Transform(
              transform: Matrix4.identity()
                ..setEntry(3, 2, 0.005)
                ..rotateY(pi * qwer.value),
              alignment: Alignment.center,  
              child: FlatButton(
                onPressed: tickfront, 
                child: Stack(
                  children: <Widget>[
                    FractionallySizedBox(
                      widthFactor: 0.5,
                      heightFactor: 0.5,
                      child: AspectRatio(
                        aspectRatio: 1 / 1.4,
                        child: Image.asset(items["frontimage"]),),
                    ),
                    IconButton(
                      iconSize: 15,
                      onPressed:() {
                        Share.share(text);
                      },
                      icon: Icon(Icons.share),
                    )
                  ]
                ),
                )
              );

      Widget b = Transform(
              transform: Matrix4.identity()
                ..setEntry(3, 2, 0.005)
                ..rotateY( -
                pi * growAnimation.value),
              alignment: Alignment.center,
              child: FlatButton(
                onPressed: tickback, 
                child: Image.asset(items["backtimage"]),
                )
              );
      Widget current = front ? a : b;

    return  Center(
      child: GestureDetector(
                onTap: tickfront, 
                onSecondaryTapCancel: tickback,
                child: current,
                )
            
   
      );
    } // Widget build
  } // CardState



